    int n;

    // Prompt the user to enter a positive integer
    std::cout << "Enter a positive integer: ";
    std::cin >> n;

    // Input validation
    if (n < 1) {
        std::cout << "Please enter a positive integer greater than zero." << std::endl;
        return 1; // Exit with error code 1
    }

    // Compute the nth number in the sequence
    int result = customSequence(n);

    // Output the result
    std::cout << "Number at position " << n << " in the custom sequence is " << result << std::endl;

    return 0; // Exit successfully
