// Recursive function to compute the nth number in the custom sequence
int customSequence(int n) {
    if (n == 1) {
        return 0; // Base case: first number in the sequence
    } else if (n == 2 || n == 3) {
        return -1; // Base case: second and third numbers in the sequence
    } else {
        // Recursive case: each number is the sum of the three preceding ones
        return customSequence(n - 1) + customSequence(n - 2) + customSequence(n - 3);
    }
