    # Initialize two pointers
    pointer1 = 0
    pointer2 = 0

    # Loop until one of the lists is exhausted or k becomes 0
    while pointer1 < len(list1) and pointer2 < len(list2) and k > 0:
        # If the current element in list1 is smaller, move pointer1
        if list1[pointer1] < list2[pointer2]:
            pointer1 += 1
        # Otherwise, move pointer2
